package Switch;

public class SampleSwitchDays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a= "orange";
		switch(a)
		{
		case "orange":
		System.out.println("orange");
		break;
		
		case "apple":
		System.out.println("apple");
		break;
		
		default:
		System.out.println("orange");	
		}
	
	}

}
